# FUNCTION TO GET A USERS INPUT AND CHECK IF IT IS VALID, AGAINST A LIST
def check_validity(valid_user_inputs):
    global users_move
    validity_tracker = False
    while validity_tracker == False:
        users_move = input("what is your move?")
        for valid_input in valid_user_inputs:
            if users_move == valid_input:
                validity_tracker = True
        if validity_tracker == False:
            print("that is not valid")

# FUNCTION TO SIMULATE TEXT TYPING OUT
def type_text(text):
    for char in text:
        sys.stdout.write(char)
        sys.stdout.flush()
        time.sleep(0.01)

# FUNCTION TO GENERATE ROOM AND SEARCH FOR ITEMS
def generate_room():
    weapon_amount = randint(0, 5)
    food_amount = randint(0, 5)
    room_type = random.choice(room_types)
    if room_type == "hells kitchen":
        print(hells_kitchen)
    elif room_type == "butchers shop":
        print(butcher_shop)
    elif room_type == "abandoned dining room":
        print(abandoned_dining_room)
    return([room_type, weapon_amount, food_amount])

# DEATH FUNCTION
def death():
    print("You died!")
    play_again = input("would you like to play again? (yes or no):")
    if play_again.lower() == "yes":
        main()
    else:
        print("Peace")

# COMBAT TO RUN SIMPLE 1V1 TURN BASED ATTACKS UNTIL ONE HAS NO HP LEFT
def combat(enemy_stats):
    global maxhp, hp, attack, enemymaxhp, enemyhp, enemyattack
    enemyhp = enemy_stats[1]
    enemymaxhp = enemy_stats[2]
    enemyattack = enemy_stats[3]
    print(f"You see an enemy. It is {enemy_stats[0]}. Combat begins!")
    turn_tracker = 0
    combat = True
    while combat == True:
        print(f"turn number {turn_tracker}")
        input("Attack!")
        enemyhp -= attack
        print(f"You attack and enemy hp is now {enemyhp}")
        if enemyhp <= 0:
            print(f"you defeated the {enemy_stats[0]}")
            combat = False
        else:
            print(f"The {enemy_stats[0]} uses {enemy_stats[4]}!")
            hp -= enemyattack
            print(f"your health is now {hp}")
            if hp <= 0:
                print(f"you have been defeated by the {enemy_stats[0]}")
                combat = False
                if enemy_stats[0] == "remy":
                    biggie_cheese()
                else:
                    death()
        turn_tracker += 1

# FUNCTION TO LET PLAYER EXPLORE
def explore():
    print("would you like to explore rooms or find enemies? 1 or 2 ?")
    check_validity(["1", "2"])
    if users_move == "1":
        global maxhp, hp, attack, weapon, food
        room_stats = generate_room()
        print(f"you see {room_stats[1]} potential weapons and {room_stats[2]} potential food items")
        user_turn = input("what is your move? 1, grab weapons or 2, eat food items?").lower()
        if user_turn == "1":
            for weapon in range(room_stats[1]):
                attack += 2
            print(f"altogether you pick up {room_stats[1]} weapons and your attack is now {attack}")
        elif user_turn == "2":
            for food in range(room_stats[2]):
                hp += 2
                if hp > maxhp:
                    hp = maxhp
            print(f"altogether you eat {room_stats[2]} items and your hp is now {hp}/{maxhp}")
    else:
        combat(random.choice([demon_enemy, hound_enemy]))






